const filterByCategory = (products, filterBy) => {
  if (filterBy === "all") {
    return products;
  }

  const newProducts = [];

  for (let product of products) {
    const isInCategory = product.metadata.category === filterBy;

    if (isInCategory) {
      newProducts.push(product);
    }
  }
  return newProducts;
};

export default filterByCategory;
