import React, { useState, useEffect } from "react";
import axios from "axios";
import "../App.css";
import Article from "./Article";

const Articles = () => {
  const [data, setData] = useState([]);

  useEffect(async () => {
    const array = async () => {
      const result = await axios("http://localhost:5000/drinks");

      setData(result.data);
    };

    array();
  }, []);

  return (
    <ul className="product-grid">
      {data.map((article) => {
        return (
          <Article
            image={article.image}
            title={article.title}
            description={article.description}
          />
        );
      })}
    </ul>
  );
};

export default Articles;
